let distanceToDestination = Number(Prompt("Enter the distance to your destination in km"));
let fuelUsage = Number(Prompt("Enter fuel usage l/km"));
let fuelPrice = Number(Prompt("Enter the fuel price"));
let theFinalPrice = 0;

theFinalPrice = (distanceToDestination / 100 * fuelUsage) * fuelPrice;

if (theFinalPrice >= 1000){
    console.log("The trip is expensive");
}


if (theFinalPrice <= 1000){
    console.log("The trip is inexpensive");
}







